import { useCallback } from "react";
import { AbstractConnector } from "@web3-react/abstract-connector";
import { UnsupportedChainIdError, useWeb3React, Web3ReactProvider } from "@web3-react/core";
import {
  NoEthereumProviderError,
  UserRejectedRequestError as UserRejectedRequestErrorInjected,
} from "@web3-react/injected-connector";
import {
  UserRejectedRequestError as UserRejectedRequestErrorWalletConnect,
  WalletConnectConnector,
} from "@web3-react/walletconnect-connector";
import { setupNetwork } from "../utils/network";
import providers, { ConnectorNames } from "../utils/providers";

const useAuth = (): {
  login: (connectorID: ConnectorNames, config: any) => void;
  logout: () => void;
  useWeb3React: any;
  Web3ReactProvider: any;
} => {
  const { activate, deactivate } = useWeb3React();
  const { connectorsByName } = providers();

  const login = useCallback(
    (connectorID: ConnectorNames, config: any) => {
      const connector = connectorsByName(connectorID, config);
      if (connector) {
        const onError = async (error: Error) => {
          console.error(error);
          if (error instanceof UnsupportedChainIdError) {
            setupNetwork(config).then(() => activate(connector as AbstractConnector));
          } else {
            localStorage.removeItem("connector");
            if (error instanceof NoEthereumProviderError) {
              // pass
            } else if (
              error instanceof UserRejectedRequestErrorInjected ||
              error instanceof UserRejectedRequestErrorWalletConnect
            ) {
              if (connector instanceof WalletConnectConnector) {
                connector.walletConnectProvider = null;
              }
              // pass
            } else {
              // pass
            }
          }
        };
        activate(connector as AbstractConnector, onError);
      }
    },
    [activate]
  );

  const logout = useCallback(() => {
    deactivate();
    if (window.localStorage.getItem("walletconnect")) {
      (connectorsByName("WalletConnect") as WalletConnectConnector).close();
      (connectorsByName("WalletConnect") as WalletConnectConnector).walletConnectProvider = null;
    }
    window.localStorage.removeItem("connector");
    window.localStorage.removeItem("wrw_config");
  }, [deactivate]);

  return { login, logout, useWeb3React, Web3ReactProvider };
};

export default useAuth;